import DatetimePicker from "vue-bootstrap-datetimepicker";

  export default {
    components: {
      DatetimePicker
    },
    data() {
      return {
        selectedDate: null
      };
    },
    computed: {
      formattedDate() {
        return this.selectedDate ? this.selectedDate.toLocaleDateString() : "";
      }
    },
    methods: {
      highlightDates(date) {
        const formatted = this.selectedDate.toLocaleDateString();

        if (formatted === "2/14/2023") {
          return "bg-danger text-white";
        }
        return "";
      }
    }
  };
