import DatetimePicker from "vue-bootstrap-datetimepicker";

export default {
  components: {
    DatetimePicker
  },
  data() {
    return {
      selectedDate: null
    };
  },
  computed: {
    formattedDate() {
      return this.selectedDate ? this.selectedDate.toLocaleDateString() : "";
    },
    highlightDates(date) {
      const holidays = [
        new Date("2023-02-06"),
        new Date("2023-02-07")
      ];

      for (let i = 0; i < holidays.length; i++) {
        if (date.getTime() === holidays[i].getTime()) {
          return "bg-danger text-white";
        }
      }
      return "";
    }
  }
};
