const mockapi=true;
import timeSheetService from '@/serviceLayer/timeSheetService';
import employees from './mockauth';

const empStore={
    state:{
        employee:{},
       employees,
       data:{}
    },
    mutations:{
    setEmployee(state,employee)
    {
        localStorage.setItem('employee',JSON.stringify(employee));
        state.employee=employee;
    },
    deleteEmp(state)
    {
        localStorage.removeItem('employee');
        state.employee="";
    },
    setdata(state,data)
    {
        console.log(data)
        state.data=data;
    }
    },
    actions:{
        SET_EMPLOYEE({commit},{success,fail,data}){
            const {empCode}=data;
            let authemp;
            if(mockapi){
                authemp=employees.findIndex((emp)=>{
                    return emp.empCode=== empCode
                });
                commit("setEmployee",employees[authemp]);
                success(employees[authemp]);    
            }
            else{
                fail("error");
            }
          },
         DELETE_EMPLOYEE({commit}){
              commit("deleteEmp");
          },

          GET_CHECKS({commit},{success,fail,data}){

            timeSheetService.ChechInCheckOut({

                success:(data) => {
                    commit('setdata', data)
                    success(data)
                },
                fail:(error) =>{
                    fail(error)
                },data
            })
          }


    },
    getters:{
        getEmployee(state){
            return state.employee;
        },
        getchecks(state)
        {
            return state.data;
        }
    },
    namespaced:true
}

export default empStore;
